    //     <button> Go back to step #index</button>
    //   )
    // })
    return (<div>asdf</div>);
    console.log('testing');
  };

  return (
    <div className="board">
      <h2>TIC TAC TOE</h2>

      <p className="status">{status}</p>
      <div className="square-row">
        {renderSquare(0)}
        {renderSquare(1)}
        {renderSquare(2)}
      </div>
      <div className="square-row">
        {renderSquare(3)}
        {renderSquare(4)}
        {renderSquare(5)}
      </div>
      <div className="square-row">
        {renderSquare(6)}
        {renderSquare(7)}
        {renderSquare(8)}
      </div>
      <div className="steps">
        <ol>
          {renderStepButtons()}
        </ol>
      </div>
    </div>
  )
}

export default Board;
