import React, {useState} from 'react';
import Square from './Square';

const Board = _ => {
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [isXnext, setXnext] = useState(true);
  return (
    <div className="board">
      <div className="square-row">
        <Square index={0} />
        <Square index={1} />
        <Square index={2} />
      </div>
      <div className="square-row">
        <Square index={3} />
        <Square index={4} />
        <Square index={5} />
      </div>
      <div className="square-row">
        <Square index={6} />
        <Square index={7} />
        <Square index={8} />
      </div>
    </div>
  )
}

export default Board;
