import React, {useState} from 'react';
import Square from './Square';
import calculateWinner from '../helpers/CalculateWinner';

const Board = _ => {
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [isXnext, setXnext] = useState(true);
  const winnerObject = calculateWinner(squares);
  const winnerHighlight = winnerObject.line;
  const handleClick = index => {
    if(squares[index] || winnerObject.winner || winnerObject.isDraw) return;
    const newSquares = [...squares];
    newSquares[index] = isXnext ? 'X' : 'O';
    setSquares(newSquares);
    setXnext(!isXnext);
  }

  let status;
  if(winnerObject.winner) {
    status = 'winner is ' + winnerObject.winner;
  } else if(winnerObject.isDraw) {
    status = 'It is a draw!';
  } else {
    status = 'Next player: ' + (isXnext ? 'X' : 'O');
  }

  const renderSquare = i => {
    return (
      <Square
        highlightWinner={i}
        value={squares[i]}
        onClick={() =>handleClick(i)}
      />
    )
  }

  return (
    <div className="board">
      <h2>TIC TAC TOE</h2>

      {status}
      <div className="square-row">
        {renderSquare(0)}
        {renderSquare(1)}
        {renderSquare(2)}
      </div>
      <div className="square-row">
        {renderSquare(3)}
        {renderSquare(4)}
        {renderSquare(5)}
      </div>
      <div className="square-row">
        {renderSquare(6)}
        {renderSquare(7)}
        {renderSquare(8)}
      </div>
    </div>
  )
}

export default Board;
